import * as React from "react";
import { StyleSheet, View, Image, Text, Pressable, Button } from "react-native";
import { useNavigation } from "@react-navigation/native";
import { Color, FontFamily, Border, FontSize } from "../../../GlobalStyles";

const Splashscreen = () => {
  const navigation = useNavigation();

  return (
      <View style={styles.root}>
      <Image
        style={styles.logoMatchup1Icon}
        resizeMode="cover"
        source={require("../../../assets/images/logo-simple.png")}
      />
      <Text style={styles.matchup}>MatchUp</Text>
      <Text style={styles.tonSportTa}>
        Ton sport, ta communauté, ton succès
      </Text>
       <Pressable
        style={[styles.signUpSection]}
        onPress={() => navigation.navigate("SignIn")}
      >
        <Text style={[styles.start]}>Start !</Text>
      </Pressable>
    </View>
  );
};

const styles = StyleSheet.create({
  root: {
    padding: 25,
    flex: 1,
    flexDirection: 'column',
    justifyContent: 'center', // Center vertically
    alignItems: 'center', // Center horizontally
},

  start: {
    fontWeight: ('bold', '300'),
        color: 'white',
        fontSize: 16,
        overflow: "hidden",
        flex: 1,

  },  
  signUpSection: {
    backgroundColor: Color.colorSteelblue,
    paddingHorizontal: 100,
    paddingBottom: 25,
    marginTop: 100,
    borderRadius: Border.br_xl,
      // box-sizing: border-box; // Not applicable in React Native
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',

      borderWidth: 1,
      shadowColor: 'rgba(158, 42, 9, 0.2)',
      shadowOffset: { width: 0, height: -7 },
      shadowOpacity: 0.2,
      shadowRadius: 7,
      height: '6vh', // 10% of the viewport height

  },
  tonSportTa: {
    alignItems: 'center',
    textAlign: "center",
    fontSize: FontSize.size_base,
    fontWeight: "700",
    fontFamily: FontFamily.openSansBold,
    color: Color.colorBlack,
    width: 330,
    height: 31,
  },
  logoMatchup1Icon: {
    alignItems: 'center',
    top: 135,
    width: 231,
    height: 246,
    position: "absolute",
    resizeMode: 'contain', // Use contain to scale the image

  },
  matchup: {
    alignItems: 'center',
    fontSize: FontSize.size_5xl,
    fontFamily: FontFamily.latoRegular,
    color: Color.colorSteelblue,
    display: "flex",
    width: 98,
    height: 56,
    textAlign: "center",
    marginTop: 100,
  },
  splashscreen: {
    width: '100%',
    height: '100%',
    overflow: "hidden",
    backgroundColor: Color.white,
    borderRadius: Border.br_xl,
  },
});



export default Splashscreen;
