import * as React from "react";
import { StyleSheet, View, Image, Text, Pressable, Button } from "react-native";
import { useNavigation } from "@react-navigation/native";
import { Color, FontFamily, Border, FontSize } from "../../../GlobalStyles";

const Splashscreen = () => {
  const navigation = useNavigation();

  return (
      <View style={styles.root}>
      <Image
        style={styles.logoMatchup1Icon}
        resizeMode="cover"
        source={require("../../../assets/images/logo-simple.png")}
      />
      <Text style={styles.matchup}>MatchUp</Text>
      <Text style={styles.tonSportTa}>
        Ton sport, ta communauté, ton succès
      </Text>
       <Pressable
        style={[styles.signUpSection]}
        onPress={() => navigation.navigate("SignIn")}
      >
        <Text style={[styles.start, styles.timeClr]}>Start !</Text>
      </Pressable>
      {/* 

      <Pressable
        style={[styles.signUpSection]}
        onPress={() => navigation.navigate("SignIn")}
      >
        <Text style={[styles.start, styles.timeClr]}>Start !</Text>
      </Pressable> */}
    </View>
  );
};

const styles = StyleSheet.create({
  root: {
    padding: 25,
    flex: 1,
    justifyContent: 'center', // Center vertically
    alignItems: 'center', // Center horizontally
},
  container: {
    backgroundColor: Color.colorSteelblue,
    paddingHorizontal: 100,
    paddingVertical: 27,
    paddingBottom: 50,
    borderRadius: Border.br_xl
  },
  timeClr: {
    color: Color.white,
    fontWeight: "600",
  },
  border: {
    height: "100%",
    width: "90.33%",
    top: "0%",
    right: "9.67%",
    bottom: "0%",
    left: "0%",
    borderRadius: 3,
    borderStyle: "solid",
    borderColor: Color.white,
    borderWidth: 1,
    opacity: 0.35,
    position: "absolute",
  },

  time: {
    marginTop: -9,
    width: "14.4%",
    top: "50%",
    left: "5.6%",
    fontSize: 15,
    letterSpacing: 0,
    lineHeight: 20,
    fontFamily: FontFamily.baloo2SemiBold,
    color: Color.white,
    fontWeight: "600",
  },
  header: {
    top: 0,
    right: 0,
    left: 0,
    height: 44,
    position: "absolute",
  },
  start: {
    fontSize: 32,
    lineHeight: 16,
    fontFamily: FontFamily.openSansSemiBold,
    textAlign: "center",
  },
  signUpSection: {
    backgroundColor: Color.colorSteelblue,
    paddingHorizontal: 100,
    paddingVertical: 27,
    paddingBottom: 25,
    padding: 50,
    borderRadius: Border.br_xl,
  },
  tonSportTa: {
    alignItems: 'center',
    textAlign: "center",
    fontSize: FontSize.size_base,
    fontWeight: "700",
    fontFamily: FontFamily.openSansBold,
    color: Color.colorBlack,
    width: 330,
    height: 31,
  },
  logoMatchup1Icon: {
    alignItems: 'center',
    top: 135,
    width: 231,
    height: 246,
    position: "absolute",
    resizeMode: 'contain', // Use contain to scale the image

  },
  matchup: {
    alignItems: 'center',
    fontSize: FontSize.size_5xl,
    fontFamily: FontFamily.latoRegular,
    color: Color.colorSteelblue,
    display: "flex",
    width: 98,
    height: 56,
    textAlign: "center",
  },
  splashscreen: {
    width: '100%',
    height: '100%',
    overflow: "hidden",
    backgroundColor: Color.white,
    borderRadius: Border.br_xl,
  },
});

export default Splashscreen;
