import * as React from "react";
import { StyleSheet, View, Image, Text, Pressable } from "react-native";
import { useNavigation } from "@react-navigation/native";
import { Color, FontFamily, Border, FontSize } from "../../../GlobalStyles";

const Splashscreen = () => {
  const navigation = useNavigation();

  return (
    <View style={styles.root}>
      <View style={styles.header}>
        <View style={styles.header}>
          <View style={styles.battery}>
            <View style={styles.border} />

            <View style={styles.capacity} />
          </View>

        </View>
      </View>
      {/* <View style={styles.footerBar} /> */}
      <Text style={[styles.tonSportTa, styles.timeFlexBox]}>
        Ton sport, ta communauté, ton succès
      </Text>
      <Image
        style={styles.logoMatchup1Icon}
        resizeMode="cover"
        source={require("../../../assets/images/MatchUp-Logo-Simple.png")}
      />
      <Image
        style={styles.logoMatchup1Icon}
        resizeMode="cover"
        source={require("../../../assets/images/Logo.png")}
      />
      <Pressable
        style={[styles.signUpSection, styles.matchupFlexBox]}
        onPress={() => navigation.navigate("SignIn")}
      >
        <Text style={[styles.start, styles.timeClr]}>Start !</Text>
      </Pressable>
      <Text style={[styles.matchup, styles.matchupFlexBox]}>MatchUp</Text>
      <Text style={[styles.matchup, styles.matchupFlexBox]}>MatchUp</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  root: {
    padding: 25,
    alignItems: 'center',
},
logo: {
    borderRadius: 100,
    width: '50%',
    maxWidth: 300,
    maxHeight: 200,
  },
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },

  timeFlexBox: {
    textAlign: "center",
    position: "absolute",
  },
