    width: "90.33%",
    top: "0%",
    right: "9.67%",
    bottom: "0%",
    left: "0%",
    borderRadius: 3,
    borderStyle: "solid",
    borderColor: Color.white,
    borderWidth: 1,
    opacity: 0.35,
    position: "absolute",
  },
  capIcon: {
    height: "35.4%",
    width: "5.58%",
    top: "32.74%",
    right: "0%",
    bottom: "31.86%",
    left: "94.42%",
    maxWidth: "100%",
    maxHeight: "100%",
    opacity: 0.4,
    position: "absolute",
    overflow: "hidden",
  },
  capacity: {
    height: "64.6%",
    width: "73.98%",
    top: "17.7%",
    right: "17.84%",
    bottom: "17.7%",
    left: "8.18%",
    borderRadius: 1,
    position: "absolute",
    backgroundColor: Color.white,
  },
  battery: {
    height: "25.68%",
    width: "6.5%",
    top: "39.32%",
    right: "3.82%",
    bottom: "35%",
    left: "89.69%",
