    height: 246,
    left: "50%",
    position: "absolute",
  },
  matchup: {
    marginLeft: -49,
    top: 398,
    fontSize: FontSize.size_5xl,
    fontFamily: FontFamily.latoRegular,
    color: Color.colorSteelblue,
    display: "flex",
    width: 98,
