import { View, Dimensions, TouchableOpacity, StyleSheet, Image, Text, ScrollView, TextInput } from 'react-native'
 import CustomInput from '../../components/customInput';
 import SignIn from '../../screens/SingIn';
import { useNavigation, useRoute } from '@react-navigation/native';
import React, { useState } from 'react';
import * as ImagePicker from 'expo-image-picker';
import { Modal } from 'react-native';
import { NavigationContainer} from '@react-navigation/native';
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import Ionicons from 'react-native-vector-icons/Ionicons'

const tab = createBottomTabNavigator();
const { width, height } = Dimensions.get('window')

function SettingsScreen(){
  return (
    <View style={styles.SettingsLightMode}>
      <Text style={styles.SettingsNotification}>
        Settings Notification Share App Privacy Policy Terms and Conditions
        Contact Feedback Logout
      </Text>
      <View style={styles.Group509}>
        <Image
          style={styles.ArrowLeft}
          source={{
            uri: "https://firebasestorage.googleapis.com/v0/b/unify-v3-copy.appspot.com/o/ryiua694bv-45%3A3356?alt=media&token=86ed02b2-ccb9-45bf-a39f-9032794db103",
          }}
        />
        <Image
          style={styles.Share}
          source={{
            uri: "https://firebasestorage.googleapis.com/v0/b/unify-v3-copy.appspot.com/o/ryiua694bv-45%3A3320?alt=media&token=5fd3a816-ef7e-431b-b1af-e7ffd69958d6",
          }}
        />
        <Image
          style={styles.Privacy}
          source={{
            uri: "https://firebasestorage.googleapis.com/v0/b/unify-v3-copy.appspot.com/o/ryiua694bv-45%3A3327?alt=media&token=6b3e9155-eb2e-4d30-ad48-56d903ad9881",
          }}
        />
        <Image
          style={styles.TermsAndPolicy}
          source={{
            uri: "https://firebasestorage.googleapis.com/v0/b/unify-v3-copy.appspot.com/o/ryiua694bv-45%3A3329?alt=media&token=369d5270-15bc-4d5b-85e1-e95ea5f0a2c1",
          }}
        />
        <Image
          style={styles.Message}
          source={{
            uri: "https://firebasestorage.googleapis.com/v0/b/unify-v3-copy.appspot.com/o/ryiua694bv-45%3A3322?alt=media&token=8fca3ea9-c1e5-4b5d-befa-e9672ab5ec09",
          }}
        />
        <Image
          style={styles.Feedback}
          source={{
            uri: "https://firebasestorage.googleapis.com/v0/b/unify-v3-copy.appspot.com/o/ryiua694bv-45%3A3325?alt=media&token=52f3289d-bf86-4f9b-ab0a-9554123ddd00",
          }}
        />
        <Image
          style={styles.Logout}
          source={{
            uri: "https://firebasestorage.googleapis.com/v0/b/unify-v3-copy.appspot.com/o/ryiua694bv-45%3A3363?alt=media&token=3e08a0fd-bdb7-4437-9ea2-7254b7a1b612",
          }}
        />
        <View style={styles.Group552}>
          <Image
            style={styles.Notification}
            source={{
              uri: "https://firebasestorage.googleapis.com/v0/b/unify-v3-copy.appspot.com/o/ryiua694bv-45%3A3351?alt=media&token=7d12b879-50dd-4517-9478-dc72ba03e5af",
            }}
          />
        </View>
      </View>
    </View>
  )
}

function FootballScreen() {
  return (
    <View style={styles.footballContainer}>
      <Text style={styles.footballTitle}>Football</Text>
      <View style={styles.footballCardContainer}>
        <View style={styles.footballCard}>
          <Image source={require('../../../assets/images/football-player2.jpg.jpg')} style={styles.footballImage} />
          <View style={styles.footballCardContent}>
            <Text style={styles.footballCardTitle}>Football Teams</Text>
            <Text style={styles.footballCardDescription}>Create your teams.</Text>
            <TouchableOpacity style={styles.footballCardButton}>
              <Text style={styles.footballCardButtonText}>Go</Text>
            </TouchableOpacity>
          </View>
        </View>
        <View style={styles.footballCard}>
          <Image source={require('../../../assets/images/football-news.png')} style={styles.footballImage} />
          <View style={styles.footballCardContent}>
            <Text style={styles.footballCardTitle}>Football Scores</Text>
            <Text style={styles.footballCardDescription}>Check out the latest football scores and results.</Text>
            <TouchableOpacity style={styles.footballCardButton}>
              <Text style={styles.footballCardButtonText}>View Scores</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>
    </View>
  );
}


function BasketballScreen(){
  return (
    <View style={styles.footballContainer}>
      <Text style={styles.footballTitle}>Football</Text>
      <View style={styles.footballCardContainer}>
        <View style={styles.footballCard}>
          <Image source={require('../../../assets/images/nba.jpg')} style={styles.footballImage} />
          <View style={styles.footballCardContent}>
            <Text style={styles.footballCardTitle}>Basketball Teams</Text>
            <Text style={styles.footballCardDescription}>Create your teams.</Text>
            <TouchableOpacity style={styles.footballCardButton}>
              <Text style={styles.footballCardButtonText}>Go</Text>
            </TouchableOpacity>
          </View>
        </View>
        <View style={styles.footballCard}>
          <Image source={require('../../../assets/images/nbafield.jpg')} style={styles.footballImage} />
          <View style={styles.footballCardContent}>
            <Text style={styles.footballCardTitle}>Basketball Scores</Text>
            <Text style={styles.footballCardDescription}>Check out the latest Basketball scores and results.</Text>
            <TouchableOpacity style={styles.footballCardButton}>
              <Text style={styles.footballCardButtonText}>View Scores</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>
    </View>
  );
}

function TennisScreen(){
  return (
    <View style={styles.container}>
      <Text> Tennis</Text>
    </View>
  );
}

const HomeScreen = () => {
  const route = useRoute();
  //const {username} = route.params;
  const [avatarImage, setAvatarImage] = useState(null);
  const [ageVisible, setAgeVisible] = useState(false);
  const [age, setAge] = useState('');
  const [cityVisible, setCityVisible] = useState(false);
  const [city, setCity] = useState('');


  const pickImage = async () => {
    const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
    if (status !== 'granted') {
      alert('Sorry, we need camera roll permissions to make this work!');
      return;
    }

    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });

    if (!result.cancelled) {
      setAvatarImage(result.uri);
    }
  };
return (
  <View style={styles.root}>
      <Text style={styles.root}> Profile</Text>

      <TouchableOpacity style={styles.avatarContainer} onPress={pickImage}>
        <Image source={avatarImage ? { uri: avatarImage } : require('../../../assets/images/circle.png')} style={styles.circle} />
      </TouchableOpacity>
      {/* <Text style={styles.username}>{username}</Text> */}
      <View style={styles.champContainer}>
        <TouchableOpacity style={styles.champLabelContainer} onPress={() => setAgeVisible(!ageVisible)}>
          <Text style={styles.champLabel}>Age:</Text>
          {ageVisible && <TextInput style={styles.champInput} placeholder="Enter age" value={age} onChangeText={text => setAge(text)} />}
          {age.length > 0 && !ageVisible && <Text style={styles.champValue}>{age}</Text>}
        </TouchableOpacity>
        <TouchableOpacity style={styles.champLabelContainer} onPress={() => setCityVisible(!cityVisible)}>
          <Text style={styles.champLabel}>City:</Text>
          {cityVisible && <TextInput style={styles.champInput} placeholder="Enter city" value={city} onChangeText={text => setCity(text)} />}
          {city.length > 0 && !cityVisible && <Text style={styles.champValue}>{city}</Text>}
        </TouchableOpacity>
        <Text style={styles.champ}>Sessions:</Text>
      </View>
      <Text style={styles.team}> Favorite Team</Text>
      <Text style={styles.team}> Favorites Sports</Text>

    </View>
);
};

const NavigationBar = () => {
  return (
    <NavigationContainer independent={true}>
    <tab.Navigator
  screenOptions={({ route }) => ({
    tabBarIcon: ({ focused, color, size }) => {
      let iconName;
      if (route.name == "Home") {
        iconName = "home";
      } else if (route.name == "Football") {
        iconName = "football-outline";
      } else if (route.name == "Basketball") {
        iconName = "basketball-outline";
      } else if (route.name == "Tennis") {
        iconName = "tennisball-outline";
      } else if (route.name == "Settings") {
        iconName = "settings-outline";
      }
      return <Ionicons name={iconName} size={size} color={focused ? "green" : "black"} />;
    },
  })}
>
  <tab.Screen name="Home" component={HomeScreen} />
  <tab.Screen name="Football" component={FootballScreen} />
  <tab.Screen name="Basketball" component={BasketballScreen} />
  <tab.Screen name="Tennis" component={TennisScreen} />
  <tab.Screen name="Settings" component={SettingsScreen} />
</tab.Navigator>
  </NavigationContainer>
  );
};

