import { View, Text } from 'react-native'
import * as React from 'react'
import CustomButton from '../CustomButton'
import * as WebBrowser from "expo-web-browser";
import * as Google from 'expo-auth-session/providers/google';
import AsyncStorage from '@react-native-async-storage/async-storage';
import Navigation from '../../navigation';
import { useNavigation, useRoute } from '@react-navigation/native';

const SocialSignInButtons = () => {

  const navigation = useNavigation();
  const [userInfo, setUserInfo] = React.useState(null);
  WebBrowser.maybeCompleteAuthSession();
  const [token, setToken] = useState("");


  const getLocalUser = async () => {
    const data = await AsyncStorage.getItem("@user");
    if (!data) return null;
    return JSON.parse(data);
  };

  const getUserInfo = async (token) => {
    //absent token
    if (!token) return;
    //present token
    try {
      const response = await fetch(
        "https://www.googleapis.com/userinfo/v2/me",
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      const user = await response.json();
      //store user information  in Asyncstorage
      await AsyncStorage.setItem("user", JSON.stringify(user));
      setUserInfo(user);
      navigation.navigate('Home');
    } catch (error) {
      console.error(
        "Failed to fetch user data:",
        response.status,
        response.statusText
      );
    }
  };

  const onSignInFacebook = () => {
    navigation.navigate('Home');
  }

  const onSignInGoogle = async () => {
    const [request, response, promptAsync] = Google.useAuthRequest({

      webClientId:"871816637044-ejvmsf74rnf29sludjqr7t60tne94sam.apps.googleusercontent.com",
      scopes: ["profile", "email"]

    });

    try {
      // Attempt to retrieve user information from AsyncStorage
      const userJSON = await AsyncStorage.getItem("user");

      if (userJSON) {
        // If user information is found in AsyncStorage, parse it and set it in the state
        setUserInfo(JSON.parse(userJSON));
        console.log("User info from AsyncStorage:", JSON.parse(userJSON));
      } else if (response?.type === "success") {
        // If no user information is found and the response type is "success" (assuming response is defined),
        // call getUserInfo with the access token from the response
        getUserInfo(response.authentication.accessToken);
        console.log("User info from Google:", response.authentication.user);
        console.log("User email:", response.authentication.user.email);
        console.log("User name:", response.authentication.user.name);
      }
    } catch (error) {
      // Handle any errors that occur during AsyncStorage retrieval or other operations
      console.error("Error retrieving user data from AsyncStorage:", error);
    }
    //add it to a useEffect with response as a dependency 
    useEffect(() => {
      onSignInGoogle();
    }, [response, token]);
    //log the userInfo to see user details
    console.log(JSON.stringify(userInfo))
    async function handleEffect() {
      const user = await getLocalUser();
      console.log("user", user);
      if (!user) {
        if (response?.type === "success") {
          // setToken(response.authentication.accessToken);
          getUserInfo(response.authentication.accessToken);
        }
      } else {
        setUserInfo(user);
        console.log("loaded locally");
      }
    }
    };

    const onSignInApple= () => {
      console.warn("Apple");
    }


  return (
  <>
      <CustomButton 
      text= "Sign In With Facebook" 
      onPress={onSignInFacebook}
      bgColor="#E7EAF4"
      fgColor="#4765A9"
      />

      <CustomButton 
      text= "Sign In With Google" 
      onPress={onSignInGoogle => {promptAsync()}}
      bgColor="#FAE9EA"
      fgColor="#DD4D44"
      />

      <CustomButton 
      text= "Sign In with Apple" 
      onPress={onSignInApple}
      bgColor="#E3E3E3"
      fgColor="#363636"
      />    
  </>
)
}

export default SocialSignInButtons
