      setEmail(data.email);
      navigation.navigate('SignIn');
    }

    return (
      <View style={styles.root}>
        <Text style= {styles.title}>Create an account
        </Text>

        <CustomInput
        name="username"
        control={control}
        placeholder= "Username"
        type={input}
        rules={{
          required: 'Username is required', 
          minLength: {
            value: 4, 
            message: 'Username should be at least 4 characters !',
          },
        }} 
        />
        <CustomInput

        name="email"
        control={control}
        placeholder= "Email" 

        />
        <CustomInput
        name="password"
        control={control}
        placeholder= "Password" 
        secureTextEntry
        rules={{
          required: 'Password is required', 
          minLength: {
            value: 4, 
            message: 'Password should be at least 4 characters !',
          },
        }} 

        />
        <CustomInput
        name="password-repeat"
        control={control}
        placeholder= "Repeat Password" 
        secureTextEntry
        rules={{
          validate: value =>
          value == pwd || 'Password do not match',
        }} 
        />

        <CustomButton text= "Register" onPress={ handleSubmit(onRegisterPressed)}/>

        <Text style={styles.text}> By registering, you confirm that you accept 
        <Text style={styles.link}> our Terms of Use </Text> and 
        <Text style={styles.link}> Privacy Policy</Text>
        </Text>

        {/* <SocialSignInButtons /> */}


        <CustomButton 
        text= "Have an Account? Sign In" 
        onPress={onSignInPressed}
        type= "TERTIARY"
        />  

      </View>


    );
  };


const styles = StyleSheet.create({
    root: {
        alignItems: 'center',
        padding: 25,

    },

    title: {
      fontSize: 24,
      fontWeight:'bold',
      color: '#051C60',
      margin: 10,
    },

    text: {
      color: 'grey',
      marginVertical: 10,
    },

    link:{
      color: '#FDB075',
    },
});


export default SignUp;
